require(['jquery', 'WeltPixel_SampleData/js/caption'], function($) {
    $('.hcaption').hcaptions({
        onshow: hideContent,
        onhide: showContent,
    });

    function hideContent()
    {
        var container = $(this.target).closest('.box-content');
        container.find('.content-wrapper').removeClass('visible');
    }
    function showContent()
    {
        var container = $(this.target).closest('.box-content');
        container.find('.content-wrapper').addClass('visible');
    }
});
